export as namespace bootstrap;

import Alert from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/alert";
import Button from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/button";
import Carousel from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/carousel";
import Collapse from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/collapse";
import Dropdown from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/dropdown";
import Modal from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/modal";
import Offcanvas from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/offcanvas";
import Popover from ".pnpm/@types+bootstrap@5.2.10/node_modules/@types/bootstrap/js/dist/popover";
